mapper.sql:
ORDER BY


		action.id ${orderAs}


		action.page ${orderAs}


		action.action_name ${orderAs}


		action.display_name ${orderAs}


		action.action_type ${orderAs}


		action.id DESC

mapper.update:
UPDATE tbl_action
action.page= #{page}, action.action_name= #{actionName}, action.display_name = #{displayName}, action.action_type = #{actionType}, url= #{url}, description= #{description},
WHERE id = #{id}
