mapper.sql:
order by


		role_id ${orderAs}


		action_id ${orderAs}


		role_id DESC

mapper.update:
update tbl_role_action
record_upd_id = #{recordUpdId}, record_upd_date = getDate(), role_id= #{role.id}, action_id= #{action.id},
role_id = #{role.id}
and action_id = #{action.id}

mapper.delete:
delete from tbl_role_action


		and role_id = ${roleId}


		and action_id = ${actionId}
